import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function CreatePlaylist(){
  const [title,setTitle] = useState('');
  const [description,setDescription] = useState('');
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try{
      const token = localStorage.getItem('token');
      const res = await axios.post('http://localhost:5000/api/playlists', { title, description }, { headers: { Authorization: 'Bearer ' + token }});
      navigate('/playlist/' + res.data._id);
    }catch(err){ alert(err.response?.data?.message || 'Error'); }
  };

  return (
    <div>
      <h2>Create Playlist</h2>
      <form onSubmit={submit} className="card">
        <input value={title} onChange={e => setTitle(e.target.value)} placeholder="Playlist Title" />
        <textarea value={description} onChange={e => setDescription(e.target.value)} placeholder="Description"></textarea>
        <button type="submit">Create</button>
      </form>
    </div>
  );
}
