import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";

export default function ViewPlaylist() {
  const { id } = useParams();
  const [playlist, setPlaylist] = useState(null);

  const fetchPlaylist = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/playlists/${id}`, {
        headers: { Authorization: localStorage.getItem("token") },
      });
      setPlaylist(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const deleteSong = async (songId) => {
    try {
      await axios.delete(`http://localhost:5000/api/playlists/${id}/songs/${songId}`, {
        headers: { Authorization: localStorage.getItem("token") },
      });

      setPlaylist({
        ...playlist,
        songs: playlist.songs.filter((song) => song._id !== songId),
      });
    } catch (err) {
      console.error("Error deleting song", err);
    }
  };

  useEffect(() => {
    fetchPlaylist();
  }, []);

  return (
    <div className="page-container">
      {playlist && (
        <div className="playlist-box">
          <h2>{playlist.title}</h2>
          <Link to={`/playlist/${id}/add`} className="add-song-link">
            + Add Song
          </Link>

          <ul>
            {playlist.songs.map((song) => (
              <li key={song._id}>
                {song.title} â€” {song.artist}
                <button className="delete-btn" onClick={() => deleteSong(song._id)}>
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
