import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Dashboard(){
  const [playlists, setPlaylists] = useState([]);

  useEffect(() => {
    const fetch = async () => {
      const token = localStorage.getItem('token');
      if(!token) return;
      try{
        const res = await axios.get('http://localhost:5000/api/playlists', { headers: { Authorization: 'Bearer ' + token }});
        setPlaylists(res.data);
      }catch(err){ console.error(err); }
    };
    fetch();
  }, []);

  return (
    <div>
      <h2>My Playlists</h2>
      <Link to="/create"><button>Create New Playlist</button></Link>
      <div style={{ marginTop:12 }}>
        {playlists.map(p => (
          <div key={p._id} className="card">
            <h3>{p.title}</h3>
            <p>{p.description}</p>
            <Link to={'/playlist/' + p._id}><button>View</button></Link>
          </div>
        ))}
        {playlists.length===0 && <p>No playlists yet. Create one!</p>}
      </div>
    </div>
  );
}
