import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

export default function ViewPlaylist(){
  const { id } = useParams();
  const [playlist, setPlaylist] = useState(null);

  useEffect(() => {
    const fetch = async () => {
      const token = localStorage.getItem('token');
      if(!token) return;
      try{
        const res = await axios.get('http://localhost:5000/api/playlists/' + id, { headers: { Authorization: 'Bearer ' + token }});
        setPlaylist(res.data);
      }catch(err){ console.error(err); }
    };
    fetch();
  }, [id]);

  if(!playlist) return <p>Loading...</p>;

  return (
    <div>
      <h2>{playlist.title}</h2>
      <p>{playlist.description}</p>
      <Link to={'/playlist/' + id + '/add'}><button>Add Song</button></Link>
      <div style={{ marginTop:12 }}>
        {playlist.songs.map(s => (
          <div key={s._id} className="card">
            <strong>{s.title}</strong> â€” {s.artist}
            {s.url && <div><audio controls src={s.url}></audio></div>}
          </div>
        ))}
        {playlist.songs.length===0 && <p>No songs. Add some!</p>}
      </div>
    </div>
  );
}
