import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";
import "../styles.css"; // ensure styling applies

export default function ViewPlaylist() {
  const { id } = useParams();
  const [playlist, setPlaylist] = useState(null);

  const fetchPlaylist = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/playlists/${id}`, {
        headers: { Authorization: localStorage.getItem("token") },
      });
      setPlaylist(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const deleteSong = async (songId) => {
    try {
      await axios.delete(`http://localhost:5000/api/playlists/${id}/songs/${songId}`, {
        headers: { Authorization: localStorage.getItem("token") },
      });

      // Update UI without refresh
      setPlaylist({
        ...playlist,
        songs: playlist.songs.filter((song) => song._id !== songId),
      });
    } catch (err) {
      console.error("Error deleting song", err);
    }
  };

  useEffect(() => {
    fetchPlaylist();
  }, []);

  if (!playlist) return null;

  return (
    <div className="page-container">
      <div className="page-card playlist-view-card">
        <h2 className="playlist-title">{playlist.title}</h2>

        <Link to={`/playlist/${id}/add`} className="blue-btn add-btn">
          + Add Song
        </Link>

        <div className="song-list">
          {playlist.songs.length === 0 && <p>No songs yet.</p>}

          {playlist.songs.map((song) => (
            <div className="song-item" key={song._id}>
              <span className="song-details">
                <strong>{song.title}</strong> â€” {song.artist}
              </span>

              <button
                className="delete-btn"
                onClick={() => deleteSong(song._id)}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
