router.post('/register', async (req, res) => {
  try {
    console.log('üü¢ Registration request received:', req.body);

    const { name, email, password } = req.body;
    if (!name || !email || !password) {
      console.log('‚ùå Missing fields in request');
      return res.status(400).json({ message: 'All fields are required' });
    }

    const existing = await User.findOne({ email });
    if (existing) {
      console.log('‚ö†Ô∏è User already exists with this email');
      return res.status(400).json({ message: 'User already exists' });
    }

    const hashed = await bcrypt.hash(password, 10);
    const user = new User({ name, email, password: hashed });
    await user.save();

    console.log('‚úÖ User saved successfully:', user);
    res.status(201).json({ message: 'User registered successfully' });

  } catch (err) {
    console.error('üí• Registration error:', err);
    res.status(500).json({ message: 'Registration failed', error: err.message });
  }
});
